import React, { Component } from 'react';
import data from "./data.json";
import ShoeItem from './ShoeItem';
import ShoesDetails from './ShoesDetails';
import ShoesList from './ShoesList';


export default class ShoesShop extends Component {
    constructor(props) {
      super(props)
    
      this.state = {
         selectedProduct : null ,
      }
    }
    // Hàm handleSelectedProduct xử lý khi người dùng click vào Shoe
    handleSelectedProduct = (product) => {
        this.setState({selectedProduct : product})
    }
  render() {
    return (
      <div className='container'>
          <h1 className='text-center'>Shoes Shop</h1>
          <ShoesList 
            products={data}
            onSelect={this.handleSelectedProduct}
          />
          <ShoesDetails product={this.state.selectedProduct}/>
      </div>
    )
  }
}
