//         <div className='card-body'>
//             <h3 className='card-title'>{product.name}</h3>
//             <p className='card-text'>{product.price}$</p>
//             <button className='btn btn-danger' onClick={() =>onSelect(product)}>Details</button>
//         </div>
//     </div>
//   )
// }
import React, { Component } from 'react'

export default class ShopItem extends Component {
    constructor(props) {
      super(props)
        const{product} = props ;
        // console.log(props.product.name);
      this.state = {
        selectedProduct : null ,
      }
    }
    onSelect = () => {
        this.setState({selectedProduct : product})
    }
  render() {
      const values = this.state
    return (
        <div className='card'>
            <img src={values.image} alt={values.name} className="card-img" width="100%" height="300px" />
            <div className='card-body'>
                <h3 className='card-title'>{values.name}</h3>
                <p className='card-text'>{values.price}$</p>
                <button className='btn btn-danger' onClick={this.onSelect}>Details</button>
            </div>
        </div>
    )
  }
}
