import React from 'react'

export default function ShoesDetails(props) {
    const {product} = props ;
    if (!product) {
        return null ;
    }
  return (
    <div className='col-sm-4'>
        <h3>{product.name}</h3>
        <img src={product.image} alt={product.name} className="card-img" width="100%" height="300px" />
    </div>
  )
}
