import React, { useState } from 'react'

export default function ShopItem(props) {
  const {product,onSelect} = props

  const [detail, onSelectDetail] = useState();

  return (
    <div className='card'>
        <img src={product.image} alt={product.name} className="card-img" width="100%" height="300px" />
        <div className='card-body'>
            <h3 className='card-title'>{product.name}</h3>
            <p className='card-text'>{product.price}$</p>
            <button className='btn btn-danger' onClick={() => onSelect(product)}>Details</button>
        </div>
    </div>
  )
}
