        }
      }
      // Hàm handleSelectedProduct xử lý khi người dùng click vào Shoe
      handleSelectedProduct = (product) => {
          this.setState({selectedProduct : product})
      }
  render() {
    return (
        <div className='row'>
        {products.map((product) => {
            return (
                <div key={product.id} className='col-sm-4'>
                    <ShopItem 
                    product={product}
                    onSelect={this.handleSelectedProduct}
                    />
                </div>
            )
        })}
